custom_css:
.navbar {
    background-color: #022646;
    color: #FFFFFF;
}

.navbar-brand > span {
    color: #FFFFFF !important;
}

.navbar-brand > span:hover {
    color: #FEB32A !important;
}

.nav-link {
    color: #FFFFFF !important;
}

.nav-link:hover {
    color: #FEB32A !important;
}

.navbar-light .navbar-brand:hover, .navbar-light .navbar-brand:focus {
  color: #FEB32A;
}
.navbar-light .navbar-brand {
    color: #FFFFFF;
}

.navbar-light .navbar-toggler {
  color: #FFFFFF;
}

.btn.btn-primary {
    background-color: #022646;
    color: #FFFFFF;
}

.btn.btn-primary:hover {
    color: #FEB32A;
}

.navbar-light .navbar-toggler .icon {
  stroke: white;
}

input[type=checkbox].disabled-selected, input[type=checkbox]:disabled:checked {
    background-color: inherit !important;
    background-image:url("data:image/svg+xml, <svg viewBox='0 0 8 7' fill='none' xmlns='http://www.w3.org/2000/svg'><path d='M1 4.00001L2.66667 5.80001L7 1.20001' stroke='black' stroke-width='1.5' stroke-linecap='round' stroke-linejoin='round'/></svg>") !important;
    background-size: inherit !important;
    border: none !important;
    box-shadow: none !important;
}
